CREATE TABLE `migrations` ( `migration` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `batch` int(11) NOT NULL)
CREATE TABLE `groups` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `name` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `permissions` text COLLATE utf8_unicode_ci, `created_at` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00', `updated_at` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00', PRIMARY KEY (`id`), UNIQUE KEY `groups_name_unique` (`name`))
CREATE TABLE `users_groups` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `user_id` int(10) unsigned NOT NULL, `group_id` int(10) unsigned NOT NULL, PRIMARY KEY (`id`))
CREATE TABLE `throttle` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `user_id` int(10) unsigned NOT NULL, `ip_address` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL, `attempts` int(11) NOT NULL DEFAULT '0', `suspended` tinyint(4) NOT NULL DEFAULT '0', `banned` tinyint(4) NOT NULL DEFAULT '0', `last_attempt_at` timestamp NULL DEFAULT NULL, `suspended_at` timestamp NULL DEFAULT NULL, `banned_at` timestamp NULL DEFAULT NULL, PRIMARY KEY (`id`), KEY `fk_user_id` (`user_id`))
