CREATE TABLE `destinations` ( `destinationId` int(10) UNSIGNED NOT NULL AUTO_INCREMENT, `destination` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `description` text COLLATE utf8_unicode_ci NOT NULL, `status` tinyint(1) UNSIGNED NOT NULL, `createdAt` datetime NOT NULL, `updatedAt` datetime NOT NULL, `categoryId` int(11) UNSIGNED NOT NULL, PRIMARY KEY (`destinationId`), KEY `destinationId` (`destinationId`), KEY `DestinationsStatus_INDEX` (`status`), KEY `destinations_categoryId_FK` (`categoryId`), CONSTRAINT `destinations_categoryId_FK` FOREIGN KEY (`categoryId`) REFERENCES `categories` (`categoryId`) ON DELETE CASCADE ON UPDATE CASCADE)
CREATE TABLE `categories` ( `categoryId` int(11) UNSIGNED NOT NULL AUTO_INCREMENT, `category` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `description` text COLLATE utf8_unicode_ci NOT NULL, `status` tinyint(1) UNSIGNED NOT NULL, `createdAt` datetime NOT NULL, `updatedAt` datetime NOT NULL, PRIMARY KEY (`categoryId`))
CREATE TABLE `package_tabs`( `packageTabId` TINYINT UNSIGNED NOT NULL AUTO_INCREMENT, `title` VARCHAR(255) NOT NULL, `description` TEXT NOT NULL, `type` TINYINT UNSIGNED NOT NULL, `packageId` INT UNSIGNED NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, PRIMARY KEY (`packageTabId`), CONSTRAINT `package_tabs_packageId_FK` FOREIGN KEY (`packageId`) REFERENCES `packages`(`packageId`) ON UPDATE CASCADE ON DELETE CASCADE)
CREATE TABLE `packages` ( `packageId` int(11) UNSIGNED NOT NULL AUTO_INCREMENT, `package` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `description` text COLLATE utf8_unicode_ci NOT NULL, `tabs` text COLLATE utf8_unicode_ci NOT NULL, `price` int(11) NOT NULL, `pdf` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `status` tinyint(1) UNSIGNED NOT NULL, `createdAt` datetime NOT NULL, `updatedAt` datetime NOT NULL, `destinationId` int(10) UNSIGNED NOT NULL, PRIMARY KEY (`packageId`), KEY `packages_destinationId_FK` (`destinationId`), CONSTRAINT `packages_destinationId_FK` FOREIGN KEY (`destinationId`) REFERENCES `destinations` (`destinationId`) ON DELETE CASCADE ON UPDATE CASCADE)
SELECT * FROM packages WHERE destinationId = $destinationId
CREATE TABLE `package_tags`( `packageTagId` SMALLINT UNSIGNED NOT NULL AUTO_INCREMENT, `tag` VARCHAR(255) NOT NULL, `type` TINYINT UNSIGNED NOT NULL, `packageId` INT UNSIGNED NOT NULL, `createdAt` DATETIME NOT NULL, PRIMARY KEY (`packageTagId`))
CREATE TABLE `destination_tabs`( `destinationTabId` TINYINT UNSIGNED NOT NULL AUTO_INCREMENT, `title` VARCHAR(255) NOT NULL, `description` TEXT NOT NULL, `type` TINYINT UNSIGNED NOT NULL, `destinationId` INT UNSIGNED NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, PRIMARY KEY (`destinationTabId`), CONSTRAINT `destination_tabs_destinationId_FK` FOREIGN KEY (`destinationId`) REFERENCES `destinations`(`destinationId`) ON UPDATE CASCADE ON DELETE CASCADE)
CREATE TABLE `pricelists`( `pricelistId` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT, `filename` VARCHAR(128) NOT NULL, `createdAt` DATETIME NOT NULL, PRIMARY KEY(`pricelistId`))
CREATE TABLE `category_images` ( `categoryImageId` int(11) UNSIGNED NOT NULL AUTO_INCREMENT, `filename` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `createdAt` datetime NOT NULL, `categoryId` int(11) UNSIGNED NOT NULL, `sort` int(11) UNSIGNED NOT NULL, `extension` varchar(11) COLLATE utf8_unicode_ci DEFAULT NULL, PRIMARY KEY (`categoryImageId`), KEY `category_images_categoryId_FK` (`categoryId`), CONSTRAINT `category_images_categoryId_FK` FOREIGN KEY (`categoryId`) REFERENCES `categories` (`categoryId`) ON DELETE CASCADE ON UPDATE CASCADE)
CREATE TABLE `package_images` ( `packageImageId` int(11) UNSIGNED NOT NULL AUTO_INCREMENT, `filename` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL, `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL DEFAULT '', `createdAt` datetime NOT NULL, `packageId` int(11) UNSIGNED NOT NULL, `sort` int(11) UNSIGNED NOT NULL, `extension` varchar(16) COLLATE utf8_unicode_ci NOT NULL, PRIMARY KEY (`packageImageId`), KEY `package_images_packageId_FK` (`packageId`), CONSTRAINT `package_images_packageId_FK` FOREIGN KEY (`packageId`) REFERENCES `packages` (`packageId`) ON DELETE CASCADE ON UPDATE CASCADE)
CREATE TABLE `pages`( `pageId` INT(8) UNSIGNED NOT NULL AUTO_INCREMENT, `title` VARCHAR(64) NOT NULL, `body` TEXT NOT NULL, `slug` VARCHAR(128) NOT NULL, `createdAt` DATETIME NOT NULL, `updatedAt` DATETIME NOT NULL, PRIMARY KEY (`pageId`))
CREATE TABLE `destination_images` ( `destinationImageId` int(10) UNSIGNED NOT NULL AUTO_INCREMENT, `filename` varchar(255) COLLATE utf8_unicode_ci NOT NULL, `createdAt` datetime NOT NULL, `destinationId` int(10) UNSIGNED NOT NULL, `sort` int(10) UNSIGNED NOT NULL, `extension` varchar(16) COLLATE utf8_unicode_ci NOT NULL, PRIMARY KEY (`destinationImageId`), KEY `destinationImageId` (`destinationImageId`), KEY `destination_images_categoryId_FK` (`destinationId`), CONSTRAINT `destination_images_categoryId_FK` FOREIGN KEY (`destinationId`) REFERENCES `destinations` (`destinationId`) ON DELETE CASCADE ON UPDATE CASCADE)
