CREATE TABLE `message` ( `id` bigint(20) unsigned NOT NULL auto_increment, `name` varchar(50) NOT NULL, `url` varchar(255) default NULL, `created` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP, `approved` bit(1) NOT NULL default '\0', `email` varchar(255) NOT NULL, `message` text NOT NULL, `ip_address` varchar(15) NOT NULL, PRIMARY KEY (`id`))
SELECT id FROM message ORDER BY id DESC LIMIT 0, 1
