CREATE TABLE chat_list ( * _id INTEGER PRIMARY KEY AUTOINCREMENT, * key_remote_jid TEXT UNIQUE, * message_table_id INTEGER * )
CREATE TABLE messages (_id INTEGER PRIMARY KEY AUTOINCREMENT, * key_remote_jid TEXT NOT NULL, * key_from_me INTEGER, * key_id TEXT NOT NULL, * status INTEGER, * needs_push INTEGER, * data TEXT, * timestamp INTEGER, * media_url TEXT, * media_mime_type TEXT, * media_wa_type TEXT, * media_size INTEGER, * media_name TEXT, * media_hash TEXT, * latitude REAL, * longitude REAL, * thumb_image TEXT, * remote_resource TEXT, * received_timestamp INTEGER, * send_timestamp INTEGER, * receipt_server_timestamp INTEGER, * receipt_device_timestamp INTEGER, * raw_data BLOB, * recipient_count INTEGER, * media_duration INTEGER, * origin INTEGER * )
SELECT * FROM messages
