CREATE TABLE release_file_new ( id INTEGER NOT NULL, created_at DATETIME, release_id INTEGER NOT NULL, filename VARCHAR(200) NOT NULL, md5_digest VARCHAR(50), size INTEGER, package_type VARCHAR(13) NOT NULL, python_version VARCHAR(25), url VARCHAR(1024), downloads INTEGER, has_sig BOOLEAN, comment_text TEXT, PRIMARY KEY (id), FOREIGN KEY(release_id) REFERENCES release (id), UNIQUE (filename), CHECK (package_type IN ('sdist', 'bdist_egg', 'bdist_msi', 'bdist_dmg', 'bdist_rpm', 'bdist_dumb', 'bdist_wininst', 'bdist_wheel')) CHECK (has_sig IN (0, 1)) )
SELECT classifier_id, release_id FROM classifier__release;
CREATE TABLE classifier__release_new ( classifier_id integer REFERENCES classifier ON DELETE CASCADE, release_id integer REFERENCES release )
