CREATE TABLE sequence( name VARCHAR(30) not null, nextid int not null, constraint pk_sequence primary key (name))
create table item ( itemid VARCHAR(10) not null, productid VARCHAR(10) not null, listprice decimal(10,2), unitcost decimal(10,2), supplier int, status VARCHAR(2), attr1 VARCHAR(80), attr2 VARCHAR(80), attr3 VARCHAR(80), attr4 VARCHAR(80), attr5 VARCHAR(80), constraint pk_item primary key (itemid), constraint fk_item_1 foreign key (productid) references product (productid), constraint fk_item_2 foreign key (supplier) references supplier (suppid))
create table supplier ( suppid int not null, name VARCHAR(80), status VARCHAR(2) not null, addr1 VARCHAR(80), addr2 VARCHAR(80), city VARCHAR(80), state VARCHAR(80), zip VARCHAR(5), phone VARCHAR(80), constraint pk_supplier primary key (suppid))
