create table CONTACT ( ID integer generated by default as identity, VERSION integer, CURRENT date not null, NEXT date, TEXT varchar(10000), PRODUCER integer, primary key (ID))
create table MOVEMENT ( WAY char(31) not null, ID integer generated by default as identity, VERSION integer, DATE date, NUMBER integer, CHARGES float, PRICE float, SOURCE varchar(255), DESTINATION varchar(255), FORMAT integer not null, WINE integer not null, CELLAR integer not null, primary key (ID))
create table BOOKING_EVENT ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, END date not null, START date not null, primary key (ID))
create table APPELLATION ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, DESCRIPTION varchar(255), LATITUDE float, LONGITUDE float, IMAGE integer, REGION integer, COUNTRY integer, primary key (ID))
create table CONFIGURATION ( ID integer generated by default as identity, VERSION integer, CONFIG_KEY varchar(255), CONFIG_VALUE varchar(2000), primary key (ID))
create table IMAGE ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, CONTENT binary(255), CONTENT_TYPE varchar(255) not null, HEIGHT integer, WIDTH integer, primary key (ID))
create table WINE_VARIETAL ( WINE integer not null, PERCENT integer, VARIETAL integer not null, primary key (WINE, VARIETAL))
create table FORMAT ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, CAPACITY float, primary key (ID))
create table REGION ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, DESCRIPTION varchar(255), LATITUDE float, LONGITUDE float, COUNTRY integer not null, IMAGE integer, primary key (ID))
create table BOOKING_QUANTITIES ( BOOKING integer not null, QUANTITY integer not null, BOOKING_BOTTLE integer not null, primary key (BOOKING, BOOKING_BOTTLE))
create table STOCK ( ID integer generated by default as identity, VERSION integer, QUANTITY integer, FORMAT integer not null, WINE integer not null, CELLAR integer not null, primary key (ID))
create table BOOKING_BOTTLE ( ID integer generated by default as identity, VERSION integer, MAX integer not null, POSITION integer not null, PRICE float not null, URL varchar(255) not null, BOOKING_EVENT integer, FORMAT integer not null, WINE integer not null, primary key (ID))
create table CELLAR ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, OWNER integer not null, primary key (ID))
create table STACK ( ID integer generated by default as identity, VERSION integer, STACK_COUNT bigint, STACK_HASH integer, STACK clob, primary key (ID))
create table VARIETAL ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, DESCRIPTION varchar(255), FLESH varchar(255), SKIN varchar(255), primary key (ID))
create table CELLAR_SHARE ( ID integer generated by default as identity, VERSION integer, ACCESS_RIGHT integer not null, EMAIL varchar(255) not null, CELLAR integer not null, primary key (ID))
create table PRODUCER ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, ADDRESS_CITY varchar(255), ADDRESS_LINE_1 varchar(255), ADDRESS_LINE_2 varchar(255), LATITUDE float, LONGITUDE float, ADDRESS_ZIP_CODE varchar(255), CONTACT_INFORMATION varchar(10000), DESCRIPTION varchar(10000), EMAIL varchar(255), FAX varchar(255), PHONE varchar(255), WEBSITE_URL varchar(255), COUNTRY integer, primary key (ID))
create table COUNTRY ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, DESCRIPTION varchar(255), LATITUDE float, LONGITUDE float, IMAGE integer, primary key (ID))
create table USER ( ID integer generated by default as identity, VERSION integer, EMAIL varchar(255) not null, FIRSTNAME varchar(255) not null, LASTNAME varchar(255) not null, PASSWORD varchar(40) not null, PROFILE varchar(255), primary key (ID))
create table BOOKING ( ID integer generated by default as identity, VERSION integer, BOOKING_EVENT integer not null, CUSTOMER integer not null, primary key (ID))
create table WINE ( ID integer generated by default as identity, VERSION integer, NAME varchar(255) not null, COLOR integer not null, DESCRIPTION varchar(255), PHOTO_URL varchar(255), RANKING varchar(255), TYPE integer not null, VINTAGE integer, APPELLATION integer not null, PRODUCER integer not null, primary key (ID))
