create table corpus ( id			bigint not null, name 		varchar(255), metaData		TEXT, last_update timestamp not null, user_id 		bigint NOT NULL, FOREIGN KEY (user_id) REFERENCES account(id), constraint pk_corpus primary key (id))
create table permission ( id bigint not null, name TEXT, url TEXT, description TEXT, revision TEXT, last_update timestamp not null, constraint pk_permission primary key (id))
create table user_roles ( user_id 	bigint not null, role_id 			bigint not null, last_update 	timestamp not null, constraint ck_user_roles primary key (user_id, role_id), foreign key(user_id)			references account(id) on delete cascade on update restrict, foreign key(role_id) 		references role(id) on delete cascade on update restrict)
create table role ( id bigint not null, name TEXT, url TEXT, permissions TEXT, description TEXT, revision TEXT, last_update timestamp not null, constraint pk_role primary key (id))
create table account ( id 	bigint not null, email varchar(255), name varchar(255), password varchar(255), field_affiliation varchar(255), url varchar(255), edit_url varchar(255), last_access varchar(255), last_login varchar(255), created varchar(255), status bigint, language varchar(255), feed_nid bigint, roles TEXT, revision TEXT, last_update timestamp not null, constraint pk_account primary key (id))
create table role_permissions ( role_id 	bigint not null, permission_id 	bigint not null, last_update 	timestamp not null, constraint ck_role_permissions primary key (role_id, permission_id), foreign key(role_id) 		references role(id) on delete cascade on update restrict, foreign key(permission_id)	references permission(id) on delete cascade on update restrict)
create table resource ( resource_id	bigint not null, corpus_id 	bigint NOT NULL, last_update timestamp not null, FOREIGN KEY (corpus_id) REFERENCES corpus(id), constraint pk_resource primary key (resource_id))
create table saved_search ( id			bigint not null, name 		varchar(255), url			TEXT, last_update timestamp not null, user_id 		bigint NOT NULL, FOREIGN KEY (user_id) REFERENCES account(id), constraint pk_saved_search primary key (id))
