CREATE TABLE IF NOT EXISTS `wechat_public_accounts` (`id` int(10) unsigned NOT NULL COMMENT '自增id', `wid` varchar(50) NOT NULL COMMENT '微信号', `name` char(50) NOT NULL COMMENT '用户名', `open_id` varchar(30) NOT NULL COMMENT 'openid', `head_url` varchar(500) NOT NULL COMMENT '头像地址', `description` varchar(500) NOT NULL COMMENT '功能介绍', `verify_info` varchar(500) NOT NULL COMMENT '认证信息', `last_article_url` varchar(500) NOT NULL COMMENT '最近文章地址', `lasttime` datetime NOT NULL COMMENT '记录时间')
CREATE TABLE IF NOT EXISTS `wechat_keywords` (`id` int(11) NOT NULL COMMENT '自增ID', `kid` bigint(20) unsigned NOT NULL COMMENT '关键词CRC32编码', `keyword` char(50) NOT NULL COMMENT '关键词', `lasttime` datetime NOT NULL COMMENT '插入时间')
